import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import api from '../services/api'

export default function Login() {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [error, setError] = useState(null)
  const navigate = useNavigate()

  const handleSubmit = async e => {
    e.preventDefault()
    setError(null)
    try {
      // Adjust endpoint if your Rails app uses a different sign-in path
      const res = await api.post('/users/sign_in', { user: { email, password } })
      // token is saved by interceptor if returned in Authorization header
      const token = localStorage.getItem('token')
      if (token) {
        navigate('/dashboard')
      } else {
        setError('Login succeeded but token not found in response headers.')
      }
    } catch (err) {
      setError(err.response?.data?.error || err.message)
    }
  }

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email</label>
          <br />
          <input value={email} onChange={e => setEmail(e.target.value)} />
        </div>
        <div>
          <label>Password</label>
          <br />
          <input type="password" value={password} onChange={e => setPassword(e.target.value)} />
        </div>
        <button type="submit">Login</button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  )
}
